type stateCreator = {
    nft: nftCreator | []
    isFetching: boolean
};

type nftCreator = [{
    id: number 
    imageUrl: string 
    name: string 
    amount: number 
    size: {} | null
    price: number
    comments: [] | null
}];

const initialState: stateCreator = {
    nft: [],
    isFetching: false
}

const productPageReducer = (state = initialState, action) => {
    if (action.type = 'SET-NFT-DATA') {
        return {
            ...state,
            nft: action.nft
        }
    }

    if (action.type === 'FETCHING-SWITCHER') {
        return {
            ...state,
            isFetching: action.isFetching
        }
    }

    return state;
}

const setNftData = (nft) => ({type: 'SET-NFT-DATA', nft})

const isFetchingCreactor = (isFetching: boolean) => ({type: 'FETCHING-SWITCHER', isFetching});


export default productPageReducer;

export const setNftDataThunk = () => (dispatch: any) => {
    dispatch(isFetchingCreactor(true));

    fetch('http://localhost:8000/Products')
        .then(response => response.json())
        .then(data => {
            dispatch(setNftData(data))
            dispatch(isFetchingCreactor(false))
        })
}
