// import NftList from "./NftList";
import React from "react";
import { useRef } from "react";
import style from "./ProductList.module.css"

const ProductList = (props) => {
    // const addImage = 'https://clipart-best.com/img/plus/plus-clip-art-115.png';
    const myRef = useRef(null);
    // const getData = myRef.current.children;

    const insertAfter = (elem, refElem) => {
        return refElem.parseNode.insertBefore(elem, refElem.nextSibling)
    }

    const sortToLow = () => {
        console.log(myRef.current.children.length)
        for (let i = 0; i < myRef.current.children.length; i++) {
            for (let n = i; n < myRef.current.children.length; n++) {
                if (+myRef.current.children[i].getAttribute('data-price') > +myRef.current.children[n].getAttribute('data-price')) {
                    let replaceNode = myRef.current.replaceChild(myRef.current.children[n], myRef.current.children[i]);
                    insertAfter(replaceNode, myRef.current.children[i]);
                }
            }
        }
    }

    const sortToHigh = () => {
        console.log(myRef.current)
        for (let i = 0; i < myRef.current.children.length; i++) {
            for (let n = i; n < myRef.current.children.length; n++) {
                if (+myRef.current.children[i].getAttribute('data-price') < +myRef.current.children[n].getAttribute('data-price')) {
                    let replaceNode = myRef.current.replaceChild(myRef.current.children[n], myRef.current.children[i]);
                    insertAfter(replaceNode, myRef.current.children[i]);
                }
            }
        }
    }   

    const sortByName = () => {
        return
    } 

    return (
        <div className={style.block}>
            <a name='products'></a>            
            <div className={style.nav}>   
                <h2>Products</h2>
                <div>
                    <div>Sort by: 
                        <button onClick={sortToLow}>Low $</button> | 
                        <button onClick={sortToHigh}>High $</button> | 
                        <button onClick={sortByName}>Name</button>
                    </div>
                </div>
            </div>

                {/* <div className={style.nftBlock}>
                    <img src={addImage} className={style.nftImage} />
                    <h3>Add new card</h3>
                    <p>Transaction Fee 5%</p>
                    <p>Sell card</p>
                    <div className={style.button}>
                        <button>Add</button>
                    </div>
                </div> */}
            <div className={style.nftPage} ref={myRef}>                
                {
                    props.nft.map(nft => 
                    <div key={nft.id} data-price={nft.price} className={style.nftBlock}>
                        <img src={nft.imageUrl} className={style.nftImage} />
                        <h3>{nft.name}</h3>
                        <p>Amount: {nft.amount}</p>
                        <p>Price: {nft.price}$</p>
                        <div className={style.button}>
                            <button>Info</button>
                            <button>Buy</button>
                        </div>
                    </div> )
                }
            </div>
        </div>
    )
}

export default ProductList;
