import ProductPage from "./ProductPage"
import { connect } from 'react-redux';
import { useState } from "react";
import { toggleModalWindow } from "../../redux/productPageReducer.tsx";

const ProductPageContainer = (props) => {

    const [confirmDelete, setconfirmDelete] = useState(false);

    const closeModalWindow = () => {
        props.isModalWindow ? props.toggleModalWindow(false) : props.toggleModalWindow(true);
    }

    const deleteNft = (ev) => {
    //     const deleteById = ev.target;
    //     const delId = deleteById.getAttribute('id');
    //     let currentCard = props.nft.find(card => card.id == id);
    }

    return (
        <div>
            {props.isModalWindow ? 
                <ProductPage 
                    deleteNft={deleteNft}
                    nft={props.nft} 
                    closeModalWindow={closeModalWindow} 
                /> : 
            null}
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        isModalWindow: state.productPage.isModalWindow,
        nft: state.productPage.nft
    }
}

export default connect(mapStateToProps, { toggleModalWindow })(ProductPageContainer);
