    }

    useEffect(() => {
        updateToggle();
        return () => clearInterval(timer);
    }, [toggle]);


    return (
        <div className={style.homepage}>
            <a name='home'></a>
            <div className={style.button}>
                <i onClick={toggleChange} className={style.left}></i>
            </div>
            {
                toggle ? 
                    <div className={style.banner}>
                        <p>Welcome to my NFT collection</p>
                        <span>You can get it for the best price</span>
                        <div className={style.line}></div>
                        <button>Buy now</button>
                    </div> 
                :
                    <div className={style.banner}>
                        <p>Discount every friday from 5p.m. to 6p.m.</p>
                        <img src="https://thumbs.dreamstime.com/b/%D0%BA%D1%80%D0%B0%D1%81%D0%BD%D1%8B%D0%B9-%D1%81%D1%82%D0%B8%D0%BA%D0%B5%D1%80-%D1%81-%D1%81%D0%BA%D0%B8%D0%B4%D0%BA%D0%BE%D0%B9-%D1%88%D0%B0%D0%B1%D0%BB%D0%BE%D0%BD-%D1%8D%D0%BC%D0%B1%D0%BB%D0%B5%D0%BC%D1%8B-%D1%81%D0%BF%D0%B5%D1%86%D0%B8%D0%B0%D0%BB%D1%8C%D0%BD%D1%8B%D0%BC-%D0%BF%D1%80%D0%B5%D0%B4%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5%D0%BC-101897007.jpg" alt="" />
                        <button>Buy now</button> 
                    </div>
            }
            <div className={style.button}>
                <i onClick={toggleChange} className={style.right}></i>
            </div>
        </div>
    )
}

export default Homepage;
